const initialState = {
    todos: []
}
function todosReducer(state = initialState, action) {
    switch (action.type) {
        case "ADDTODO":
            return {
                todos: [...state.todos, action.payload]
            }
        case "DELETETODO":
            return {
                todos: [...state.todos.filter((item) => item !== action.payload)]
            }
        default :
        return state;
    }
}
export default todosReducer;
