import React, { useState } from 'react'
import { Box, Button, Card, Grid, Input, Paper, Typography, styled } from '@mui/material'
import { addTodo } from '../../../redux/actions'
import { useDispatch, useSelector } from 'react-redux'

const MainComponent = () => {
    const dispatch = useDispatch()
    const [task, setTask] = useState("")
    const todos = useSelector(state => state.todos)
    const handleAddTask = () => {
        dispatch(addTodo(task))
    }

    const Item = styled(Paper)(({ theme }) => ({
        backgroundColor: '#1A2027',
        ...theme.typography.body2,
        padding: theme.spacing(4),
        textAlign: 'center',
        color: 'white',
    }));
    return (
        <>
            <div style={{ textAlign: 'center' }}>
                <Typography variant='body1'>
                    HEADING LEADS
                </Typography>
                <Input type='text' onChange={(event) => { setTask(event.target.value) }} />
                <Button variant='contained' size='small' onClick={handleAddTask}>add Task</Button>
            </div>
            <Grid container>
                {
                    todos.length ? todos.map((item, ind) => (
                        <Grid xs={4}>
                            <Item key={ind}>{item}</Item>
                        </Grid>
                    )) : null
                }
            </Grid>
        </>
    )
}

export default MainComponent
