const initialState = {
    todos: []
}
function todosReducer(state = initialState, action) {
    switch (action.type) {
        case "ADDTODO":
            return {
                todos: [...state.todos, action.payload]
            }
        case "DELETETODO":
            return {
                todos: [...state.todos.filter((item) => item !== action.payload)]
            }
        case "UPDATETASK":
            return {
                todos: [...state.todos.map((item) => {
                    console.log('coming into the actions ==>',action);
                    if (item == action.payload.item) {
                       return item[action.payload.index] = item.payload.newItem
                    }
                }
                )]
            }
        default:
            return state;
    }
}
export default todosReducer;
