import React, { useState } from 'react'
import { Box, Button, Card, Grid, Input, Paper, Typography, styled } from '@mui/material'
import { addTodo } from '../../../redux/actions'
import { useDispatch, useSelector } from 'react-redux'

const MainComponent = () => {
    const dispatch = useDispatch()
    const [task, setTask] = useState("")
    const todos = useSelector(state => state.todos)
    const handleAddTask = () => {
        dispatch(addTodo(task))
    }

    const Item = styled(Paper)(({ theme }) => ({
        backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
        ...theme.typography.body2,
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      }));
    function FormRow() {
        return (
          <React.Fragment>
            <Grid item xs={4}>
              <Item>Item</Item>
            </Grid>
            <Grid item xs={4}>
              <Item>Item</Item>
            </Grid>
            <Grid item xs={4}>
              <Item>Item</Item>
            </Grid>
          </React.Fragment>
        );
      }
    return (
        <>
            <div style={{ textAlign: 'center' }}>
                <Typography variant='body1'>
                    HEADING LEADS
                </Typography>
                <Input type='text' onChange={(event) => { setTask(event.target.value) }} />
                <Button variant='contained' size='small' onClick={handleAddTask}>add Task</Button>
            </div>
            {/* <Grid container rowGap={3}> */}
            {
                todos.length ? todos.map((item, ind) => (
                    <>
                        <Box sx={{ flexGrow: 1 }}>
                            <Grid container spacing={1}>
                                <Grid container item spacing={3}>
                                    <FormRow />
                                </Grid>
                                <Grid container item spacing={3}>
                                    <FormRow />
                                </Grid>
                                <Grid container item spacing={3}>
                                    <FormRow />
                                </Grid>
                            </Grid>
                        </Box>
                    </>

                )) : null
            }
            {/* </Grid> */}
        </>
    )
}

export default MainComponent
